function updateShip(){

		var ship = scene.getObjectByName( "ship2-10" );
		shipY = ship.position.y;
		if(ship.position.y > 400 ){
			shipAcceleration-=4;
		}
		if(ship.position.y < -400){
			shipAcceleration+=4;
		}
		//shipAcceleration = 0;
	for ( k = 0 ; k < 5 ; k++ ){
		for( i = 0 ; i < shipSize[k] ; i++ ){

			var object = scene.getObjectByName( "ship"+k+"-"+i );

			object.position.y += shipAcceleration;
		}		
	}
}

